<?php// widget for latest productclass wpb_latest_class extends wp_widget {	function __construct(){		$params =   array(			'description' => 'Display a latest product slider on your sidebar.',            'name'        => 'WPB Latest Product Slider'		);		parent::__construct('wpb_latest_class','',$params);	}		// setup the widget form    public function  form($instance){        extract($instance);?>        <p>            <label for="<?php echo $this->get_field_id('title');?>">Title: </label>            <input				class="widefat"				id="<?php echo $this->get_field_id('title');?>"				name="<?php echo $this->get_field_name('title');?>"				value="<?php if(isset($title)) echo esc_attr($title);?>" 			/>		</p>		<p> 			<label for="<?php echo $this->get_field_id('number_of_product');?>">Number of Product:</label>			<input style="max-width:30%;display:block;"				class="widefat"				type="number"				min="1"				id="<?php echo $this->get_field_id('number_of_product');?>"				name="<?php echo $this->get_field_name('number_of_product');?>"				value="<?php if(isset($number_of_product)) echo esc_attr($number_of_product);?>"			/>		</p><?php	}	// setup the widget content    public function widget($args,$instance){				extract($args);		extract($instance);				$title = apply_filters('widget_title',$title);		$number_of_product = apply_filters('widget_description',$number_of_product);				echo $before_widget;			echo $before_title.$title.$after_title;			echo do_shortcode("[wpb-sidebar-latest-product posts=$number_of_product]");		echo $after_widget;	    }}// hook the latest widgetadd_action('widgets_init','wpb_register_latest_pro');function wpb_register_latest_pro(){    register_widget('wpb_latest_class');}// widget for feature productclass wpb_feature_class extends wp_widget {	function __construct(){		$params =   array(			'description' => 'Display a feature product slider on your sidebar.',            'name'        => 'WPB Feature Product Slider'		);		parent::__construct('wpb_feature_class','',$params);		}		//setup widget form    public function  form($instance){        extract($instance);?>        <p>            <label for="<?php echo $this->get_field_id('title');?>">Title: </label>            <input				class="widefat"				id="<?php echo $this->get_field_id('title');?>"				name="<?php echo $this->get_field_name('title');?>"				value="<?php if(isset($title)) echo esc_attr($title);?>"			/>		</p>		<p> 			<label for="<?php echo $this->get_field_id('number_of_product');?>">Number of Product:</label>			<input style="max-width:30%;display:block;"				class="widefat"				type="number"				min="1"				id="<?php echo $this->get_field_id('number_of_product');?>"				name="<?php echo $this->get_field_name('number_of_product');?>"				value="<?php if(isset($number_of_product)) echo esc_attr($number_of_product);?>"			/>		</p><?php    }	//setup widget content    public function widget($args,$instance){		extract($args);		extract($instance);				$title = apply_filters('widget_title',$title);		$number_of_product = apply_filters('widget_description',$number_of_product);				echo $before_widget;			echo $before_title.$title.$after_title;			echo do_shortcode("[wpb-sidebar-feature-product posts=$number_of_product]");		echo $after_widget;		}}// hook the feature widgetadd_action('widgets_init','wpb_register_feature_pro');function wpb_register_feature_pro(){    register_widget('wpb_feature_class');}